import React, { useState } from "react";
import { AllWrapper } from "../AllWrapper"; // <-- correctly imported

export const FrameWrapper = () => {
  const [selectedButton, setSelectedButton] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);

  const handleButtonClick = (buttonName) => {
    if (buttonName === "All") {
      setShowDropdown(!showDropdown);
    } else {
      setSelectedButton(buttonName);
      setShowDropdown(false);
    }
  };

  return (
    <div className="flex flex-col items-start w-[1380px] mx-auto mt-10 relative">
      <div className="flex items-center gap-4">
        {/* All Button */}
        <div
          onClick={() => handleButtonClick("All")}
          className={`flex items-center gap-2 px-4 py-2 rounded-3xl border ${
            selectedButton === "All" ? "border-blue-500 bg-blue-100" : "border-[#dddddd]"
          } cursor-pointer hover:bg-gray-100`}
        >
          <img
            src="https://c.animaapp.com/ma15u5wlRvwvT0/img/bars-filter.svg"
            alt="All"
            className="w-5 h-5"
          />
          <span className="font-medium text-sm text-[#2f2f2f]">All</span>
        </div>

        {/* Other Buttons */}
        {[
          "Laser Safety",
          "Authorized Representative",
          "Testing Laboratory",
          "Battery",
          "Calibration Laboratory",
        ].map((label, idx) => (
          <div
            key={idx}
            onClick={() => handleButtonClick(label)}
            className={`flex items-center gap-2 px-4 py-2 rounded-3xl border ${
              selectedButton === label ? "border-blue-500 bg-blue-100" : "border-[#dddddd]"
            } cursor-pointer text-[#2f2f2f] text-sm hover:bg-gray-100`}
          >
            {label}
          </div>
        ))}
      </div>

      {/* Show AllWrapper if "All" clicked */}
      {showDropdown && <AllWrapper />}
    </div>
  );
};
