import React, { useState, useEffect } from "react";
import axios from "axios";  // For making HTTP requests
import { LabOverview } from "../../Lab Overview/LabOverview";

export const DivWrapper = ({ onViewProfile }) => {
  const [labs, setLabs] = useState([]);  // State to store lab data
  const [showLabOverview, setShowLabOverview] = useState(false);

  // Fetch lab data when component mounts
  useEffect(() => {
    const fetchLabs = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/labs'); // Adjust URL based on your backend
        setLabs(response.data.data);  // Set labs data from the backend
        console.log(response.data.data); // Log the fetched data for debugging
      } catch (error) {
        console.error('Error fetching lab data', error);
      }
    };

    fetchLabs(); // Fetch data on mount
  }, []);  // Empty dependency array ensures this runs only once when the component mounts

  return (
    <>
      {showLabOverview ? (
        <LabOverview />
      ) : (
        <div className="flex flex-col w-[1580px] items-start gap-5 absolute top-[441px] left-[60px]">
          {labs.length === 0 ? (
            <div>Loading labs...</div>  // Show loading message if labs data is not yet fetched
          ) : (
            labs.map((lab) => (
              <div
                key={lab.id}
                className="flex flex-col w-[1060px] h-[276px] items-start gap-2.5 p-8 relative bg-white rounded-2xl border-[0.5px] border-solid border-[#cbcbcb] shadow-[0px_0px_15px_#0000000d]"
              >
                <div className="inline-flex flex-col items-start gap-2.5 relative flex-[0_0_auto] mr-[-1.00px]">
                  {/* Lab Data Display */}
                  <div className="flex items-end relative self-stretch w-full flex-[0_0_auto]">
                    <div className="flex flex-col w-[967px] h-[104px] items-start gap-2.5 relative">
                      <div className="flex w-[969px] h-[188px] items-start gap-[18px] relative mb-[-84.00px] mr-[-2.00px]">
                        <div className="flex w-[969px] items-center gap-[18px] relative">
                          <img
                            className="relative w-[106px] h-[106px] object-cover"
                            alt="Logo"
                            src={lab.logo} // Use lab logo from response
                          />
                          <div className="flex flex-col w-[845px] h-[106px] items-start gap-1.5 relative">
                            <div className="flex items-start justify-between relative self-stretch w-full flex-[0_0_auto]">
                              <div className="inline-flex items-center gap-1.5 relative flex-[0_0_auto]">
                                <div className="relative w-fit mt-[-1.04px] [font-family:'Nunito_Sans',Helvetica] font-bold text-[#202224] text-xl tracking-[-0.12px] leading-[normal]">
                                  {lab.company_name}  {/* Update to use 'company_name' */}
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Additional lab details like industry/applications, testing services */}
                <div>
                  <p>{lab.description}</p>
                  <div>
                    <strong>Accreditation:</strong> {lab.accreditation}
                  </div>
                  <div>
                    <strong>Location:</strong> {lab.location}
                  </div>
                  <div>
                    <strong>Expertise:</strong> {lab.expertise}
                  </div>
                  <div>
                    <strong>Industries:</strong> {lab.industries.join(", ")}
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </>
  );
};
