import React, { useState } from "react";

// AllWrapper Component (Dropdown Content)
const AllWrapper = () => {
  return (
    <div className="flex flex-col bg-white border p-4 rounded shadow-md mt-2 w-[600px]">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          <img src="https://c.animaapp.com/ma1aqe6wFlEgiX/img/filter-list.svg" alt="Filter" className="w-6 h-6" />
          <span className="font-bold text-lg text-[#202224]">Filters</span>
        </div>
        <img src="https://c.animaapp.com/ma1aqe6wFlEgiX/img/x-1.svg" alt="Close" className="w-6 h-6 cursor-pointer" />
      </div>

      <hr className="border-gray-300" />

      <div className="flex gap-4 mt-4">
        {/* Left Side Menu */}
        <div className="flex flex-col gap-2 w-1/2">
          {[
            "Lab Accreditation",
            "Business Categories",
            "Testing Services",
            "Product Categories",
            "Standards Filter",
            "Regions Filter",
          ].map((item, idx) => (
            <div
              key={idx}
              className="flex items-center gap-2 p-2 rounded hover:bg-gray-100 cursor-pointer"
            >
              <img
                src="https://c.animaapp.com/ma1aqe6wFlEgiX/img/category.svg"
                alt={item}
                className="w-5 h-5"
              />
              <span className="text-gray-700">{item}</span>
            </div>
          ))}
        </div>

        {/* Right Side Tags */}
        <div className="flex flex-wrap gap-2 w-1/2">
          {[
            "Product Safety",
            "Radio",
            "Laser Safety",
            "Battery",
            "Testing Laboratory",
            "Calibration Laboratory",
            "Authorized Representative",
            "EMC",
          ].map((tag, idx) => (
            <div
              key={idx}
              className={`px-3 py-1 rounded-3xl border ${
                idx === 0 ? "bg-[#6161ff] text-white" : "border-gray-300 text-gray-700"
              } text-sm`}
            >
              {tag}
            </div>
          ))}
        </div>
      </div>

      {/* Bottom Buttons */}
      <div className="flex gap-4 justify-end mt-4">
        <button className="px-4 py-2 rounded-3xl border border-[#e93a3a] text-[#e93a3a] font-medium">
          Clear
        </button>
        <button className="px-4 py-2 rounded-3xl bg-[#e93a3a] text-white font-medium">
          Apply
        </button>
      </div>
    </div>
  );
};

// FrameWrapper (Main Component)
export const FrameWrapper = () => {
  const [showDropdown, setShowDropdown] = useState(false);

  return (
    <div className="flex flex-col items-start w-[1380px] mx-auto mt-10 relative">
      <div className="flex items-center gap-4">
        {/* All Button */}
        <div
          onClick={() => setShowDropdown(!showDropdown)}
          className="flex items-center gap-2 px-4 py-2 rounded-3xl border border-[#dddddd] cursor-pointer hover:bg-gray-100"
        >
          <img
            src="https://c.animaapp.com/ma15u5wlRvwvT0/img/bars-filter.svg"
            alt="All"
            className="w-5 h-5"
          />
          <span className="font-medium text-sm text-[#2f2f2f]">All</span>
        </div>

        {/* Other Buttons */}
        {[
          "Laser Safety",
          "Authorized Representative",
          "Testing Laboratory",
          "Battery",
          "Calibration Laboratory",
        ].map((label, idx) => (
          <div
            key={idx}
            className="flex items-center gap-2 px-4 py-2 rounded-3xl border border-[#dddddd] text-[#2f2f2f] text-sm"
          >
            {label}
          </div>
        ))}
      </div>

      {/* Show AllWrapper if All button clicked */}
      {showDropdown && <AllWrapper />}
    </div>
  );
};
